import React, { useState } from "react";

function Upload() {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false); // üîÑ Loading state

  const handleUpload = async () => {
    if (!file) {
      setMessage("Please select a file first.");
      return;
    }

    setLoading(true); // ‚è≥ Show loading
    setMessage("");   // Clear any previous message

    try {
      const formData = new FormData();
      formData.append("file", file);

      const response = await fetch("http://localhost:8000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        setMessage("‚úÖ Upload complete: " + data.filename);
      } else {
        setMessage("‚ùå Error: " + (data.error || "Upload failed"));
      }
    } catch (err) {
      setMessage("‚ö†Ô∏è Upload error: " + err.message);
    } finally {
      setLoading(false); // ‚úÖ Done loading
    }
  };

  return (
    <div>
      <h3>üìÑ Upload File</h3>
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <button onClick={handleUpload} disabled={loading}>
        {loading ? "Uploading..." : "Upload"}
      </button>
      {message && <p>{message}</p>}
    </div>
  );
}

export default Upload;
